export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const [open, setOpen] = useState<boolean>(false);

  useEffect(() => {
    const script = document.createElement("script");
    script.id = "mcjs";
    script.async = true;
    script.src =
      "https://chimpstatic.com/mcjs-connected/js/users/a9ac28d2ddb42e6e193db6b7a/3a72bda09f01ef63679b8d268.js";
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  return (
    <html lang="en">
      <body>
        <div className={"mainWrapper h-[100vh] w-[100vw] flex overflow-hidden"}>
          <SideNav open={open} setOpen={setOpen} />
          {/* dynamic width */}
          <div className={` ${open && "navOpened"} pageWrapper`}>
            {/* header */}
            <SubscribedServices />
            <div className="pageContent">{children}</div>
          </div>
        </div>
      </body>
    </html>
  );
}
